INSERT INTO `tb_article_copy` VALUES ('343', '5', '大峪中学', '<p>北京市大峪中学始建于1946年，1978年列为北京市重点中学，2004年进入北京市普通高中示范校行列。是一所校风优良、质量上乘的北京市重点中学，莘莘学子向往的京西名校。学校位于九龙山下，永定河畔，北京门头沟城镇中心的滨河德露苑18号，新的开发区内。</p><p><br/></p><p>学校地址：门头沟区滨河德露苑18号</p><p>邮政编码：102300</p><p>联系电话：61864110</p><p>学校网址：http://www.dyzx-bj.com/</p>', '11', '0', '11', '1', '1', '2', '9', null, 'download/image_url/20150526_140229_544065.jpg', null, null, null, '10', '2015-05-24', '系统管理员', null, null, '2015-05-24 23:22:24', '2015-05-24 23:22:24', '1');
