INSERT INTO `tb_article_copy` VALUES ('2408', '230', '英雄联盟', '<p>&nbsp;</p>', '8', '0', '11', '1', '1', '2', '10', null, null, 'http://i4.tietuku.cn/48fb17b99984db50.jpg', null, null, '10', '2016-01-20', '系统管理员', null, null, '2016-01-20 17:02:55', '2016-01-20 17:02:55', '1');
INSERT INTO `tb_article_copy` VALUES ('2409', '230', '德玛西亚皇子', '<p>德玛西亚皇子,别名四阿哥,周杰伦,嘉文四世。</p><p>E-Q技能的存在为嘉文带来了不错的机动性，还附带三种控制技能让他成为一个让人十分头疼的坦克，</p><p>你无法在第一时间秒掉他，却也无法忽视他的存在，是一个团队价值非常高的英雄。</p>', '3', '0', '11', '1', '1', '2', '11', null, null, 'http://i4.tietuku.cn/a1babff3fc98c6c3.jpg', null, null, '10', '2016-01-20', '系统管理员', null, null, '2016-01-20 17:04:15', '2016-01-20 17:04:15', '1');
INSERT INTO `tb_article_copy` VALUES ('2410', '230', '德邦总管', '<p>德邦总管,别名赵信,菊花信。</p><p>赵信很适合做团战发起人，第一时间冲锋加大招尽量多的造成伤害。</p><p>至少加1点战嚎来减少技能的冷却。对于赵信来说，攻速道具是收益最高的选择。缺点赵信是一个只进不退的英雄。</p>', '1', '0', '11', '1', '1', '2', '12', null, null, 'http://i4.tietuku.cn/56d0d1fceb659858.jpg', null, null, '10', '2016-01-20', '系统管理员', null, null, '2016-01-20 17:04:59', '2016-01-20 17:04:59', '1');
INSERT INTO `tb_article_copy` VALUES ('2411', '230', '黑暗之女', '<p>黑暗之女,别名火女,萝莉,安妮。</p><p>有着被动技能和Q技能的回蓝特效让安妮在对线期就能占尽优势，可以在前期保证自己的经济，</p><p>而技能本身的固定伤害让安妮在到达六级后就可以用一套连招给对方带来极大伤害甚至秒掉对手。</p>', '0', '0', '11', '1', '1', '2', '13', null, null, 'http://i4.tietuku.cn/5ec8c315c1f879ec.jpg', null, null, '10', '2016-01-20', '系统管理员', null, null, '2016-01-20 17:05:33', '2016-01-20 17:05:33', '1');
INSERT INTO `tb_article_copy` VALUES ('2412', '230', '皎月女神', '<p>皎月女神,别名皎月,黛安娜。</p><p>戴安娜作为一个近战法师，有着其他AP难以企及的爆发，同时还有突进、控制以及不错的生存能力。</p><p>但缺点也很明显，作为一个法师却是近战，而且又没有逃生技能，如果团战的时机切入时机错误，经常会有去无回。</p>', '2', '0', '11', '1', '1', '2', '14', null, null, 'http://i4.tietuku.cn/ccf60db2767080e8.jpg', null, null, '10', '2016-01-20', '系统管理员', null, null, '2016-01-20 17:05:59', '2016-01-20 17:05:59', '1');
INSERT INTO `tb_article_copy` VALUES ('2413', '230', '寒冰射手', '<p>寒冰射手,别名寒冰,艾希,爱射,冰弓,冰女。</p><p>寒冰射手艾希是一个能力全面的防守型ADCarry，她的W可以进攻性地推线，也是个配合她的Q进行风筝的强力技能。</p>', '0', '0', '11', '1', '1', '2', '15', null, null, 'http://i4.tietuku.cn/9346cade1e9640a5.jpg', null, null, '10', '2016-01-20', '系统管理员', null, null, '2016-01-20 17:06:31', '2016-01-20 17:06:31', '1');
INSERT INTO `tb_article_copy` VALUES ('2414', '230', '联系我们', '<p>作者：Fly的狐狸</p><p>QQ：330627517</p><p><a target=\"_blank\" href=\"http://mail.qq.com/cgi-bin/qm_share?t=qm_mailme&email=_crKyc-LzszIzrmIiNealpQ\" style=\"text-decoration:none;\"><img src=\"http://rescdn.qqmail.com/zh_CN/htmledition/images/function/qm_open/ico_mailme_02.png\"/></a></p>', '1', '0', '11', '1', '1', '2', '16', null, null, 'http://i4.tietuku.cn/400844eb3b32e353.jpg', null, null, '10', '2016-01-20', '系统管理员', null, null, '2016-01-20 17:07:05', '2016-01-20 17:07:05', '1');
INSERT INTO `tb_article_copy` VALUES ('3001', '241', '个人介绍', '<p style=\"text-align: center; font-family: 微软雅黑, Verdana, sans-serif, 宋体;\"><img src=\"http://ww3.sinaimg.cn/mw690/3fc7e281jw1ep7p6ei1g8j20sg0lcgxc.jpg\" _src=\"http://ww3.sinaimg.cn/mw690/3fc7e281jw1ep7p6ei1g8j20sg0lcgxc.jpg\" style=\"width: 410px; height: 319px;\"/></p><p>&nbsp; &nbsp; 本人从事Java Web开发工作，有较丰富的4A、工资社保、军交运输(GIS)、短信平台项目开发经验；有丰富的linux、unix部署、调试经验；有独立开发设计项目的经验。</p><p>&nbsp; &nbsp; 经常通过开源中国（<a href=\"http://www.oschina.net\" target=\"_blank\" title=\"oschina\">www.oschina.net</a>）等网站学习新的技术，如Jfinal、beelt、fastjson、afinal等；曾阅读一些书籍和框架源码，如《Spring3.x企业应用开发实践》、《锋利的Jquery》、Jfinal源码、fastjson等；并在工作期间开发过安盟双因素认证，支付宝，微信自动回复、Oauth2、短信（cmpp、smgp、sgip）等外部接口。</p>', '1739', '25', '12', '1', '1', '2', '5', null, '20150520_113828_124810.jpg', null, null, null, '10', '2014-03-05', '系统管理员', '2015-01-29', '2015-01-23', '2015-01-28 17:29:55', '2015-01-28 17:29:55', '1');
INSERT INTO `tb_article_copy` VALUES ('3010', '248', 'Oauth2.0认证', '<meta property=\"qc:admins\" content=\"37460026076264167063757715402\" />', '125', '0', '13', '1', '1', null, '10', null, null, null, null, null, '10', '2015-02-15', '系统管理员', null, null, '2015-02-15 14:11:45', '2015-02-15 14:11:45', '1');
INSERT INTO `tb_article_copy` VALUES ('3103', '243', '金钱管理 jfinal money', '<p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><span style=\"line-height: 1.5; font-size: 10pt;\">这是一个java开发的金钱管理软件，本着帮助新人以及为学习新技术的态度。</span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">JFinal-Money采用了简洁强大的JFinal作为web框架，加入了代码自动生成功能。</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">模板引擎用：Beetl（从JSP迁移到Beetl）</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">数据库：sqlite3，mysql和postgre</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">前端框架：bootstrap3，移动端Jquery Mobile</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><span style=\"line-height: 1.5; font-size: 10pt;\">运行效果：<a href=\"http://jmoney.sturgeon.mopaas.com/web\" target=\"_blank\" style=\"color: rgb(62, 98, 166); outline: 0px;\">http://jmoney.sturgeon.mopaas.com/web</a></span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">源码地址：<a href=\"http://git.oschina.net/flyfox/jmoney\" target=\"_blank\" style=\"color: rgb(62, 98, 166); outline: 0px;\">http://git.oschina.net/flyfox/jmoney</a></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">感谢&nbsp;孤独的√3 的帮助，以及开源Jfinal，Beelt</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">首页效果图如下：</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><img src=\"http://ww2.sinaimg.cn/mw690/3fc7e281jw1eqebcfti0nj20r60ft760.jpg\"/></p>', '575', '1', '12', '1', '1', null, '10', null, null, null, null, null, '10', '2014-06-05', '系统管理员', null, null, '2015-01-28 17:30:23', '2015-01-28 17:30:23', '1');
INSERT INTO `tb_article_copy` VALUES ('3105', '242', '基础单词', '<p><span style=\"line-height: 1.42857143;\">LOL</span><br/></p><p><br/></p><p>firstblood 第一滴血</p><p>An enemy has been slain 一个敌人被击杀</p><p>double kill 双杀</p><p>triple kill 三杀&nbsp;</p><p>quadra kill 四杀&nbsp;</p><p>penta kill 五杀&nbsp;</p><p>killing spree 大杀特杀</p><p>rampage 暴走</p><p>unstoppable 无人可挡</p><p>dominating 主宰比赛</p><p>godlike 神一般</p><p>legendary 超神</p><p><br/></p><p>星期 &nbsp;&nbsp;</p><p><br/></p><p>星期一： Mon.=Monday</p><p>星期二： Tues.=Tuesday</p><p>星期三：Wed.=Wednesday</p><p>星期四： Thur.=Thursday</p><p>星期五： Fri.=Friday</p><p>星期六： Sat.=Saturday&nbsp;</p><p>星期天： Sun.=Sunday</p><p><br/></p><p>月份</p><p><br/></p><p>一月份：Jan.=January</p><p>二月份：Feb.=February</p><p>三月份：Mar.=March&nbsp;</p><p>四月份：Apr.=April&nbsp;</p><p>五月份： May=May</p><p>六月份：Jun.=June</p><p>七月份：Jul.=July</p><p>八月份： Aug.=August&nbsp;</p><p>九月份： Sept.=September</p><p>十月份：Oct.=October</p><p>十一月份：Nov.=November</p><p>十二月份：Dec.=December</p><p><br/></p>', '226', '2', '11', '1', '1', null, '10', null, null, null, null, null, '10', '2015-01-28', '系统管理员', null, null, '2015-01-28 17:48:26', '2015-01-28 17:48:26', '1');
INSERT INTO `tb_article_copy` VALUES ('3106', '245', '关于我们', '<p>作者：FLY的狐狸</p><p>地址：北京市</p><p>联系方式：qq(369191470),请注明来源和添加原因</p><p><a target=\"_blank\" href=\"http://mail.qq.com/cgi-bin/qm_share?t=qm_mailme&email=allcU1tTW15dWiobG0QJBQc\" style=\"text-decoration:none;\"><img src=\"http://rescdn.qqmail.com/zh_CN/htmledition/images/function/qm_open/ico_mailme_02.png\"/></a></p>', '414', '1', '12', '1', '1', '2', '11', null, 'jflyfox/blog/article_image/20160611_162651_964248.png', null, '', '', '10', '2015-01-29', '系统管理员', null, null, '2015-01-29 14:04:52', '2015-01-29 14:04:52', '1');
INSERT INTO `tb_article_copy` VALUES ('3107', '243', 'MP3音乐播放器FFPlayer', '<p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">FFPlayer 是一个通过&nbsp;<a target=\"_blank\" href=\"http://www.oschina.net/p/javafx\" style=\"color: rgb(62, 98, 166); outline: 0px;\">JavaFX</a>&nbsp;实现的MP3音乐播放器。</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">功能列表如下</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><br/></p><ol class=\" list-paddingleft-2\" style=\"margin-top: 0.5em; margin-bottom: 0.5em; margin-left: 1.5em; list-style-position: inside; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><li><p style=\"margin-top: 0px; margin-bottom: 10px; display: inline;\"><span style=\"line-height: 1.5; font-size: 10pt;\">支持播放、暂停、上一首、下一首，时间轨，播放模式，音量调节；</span></p></li><li><p style=\"margin-top: 0px; margin-bottom: 10px; display: inline;\"><span style=\"line-height: 1.5; font-size: 10pt;\">播放列表暂时只支持拖拽添加，右键可以删除列表音乐；</span></p></li><li><p style=\"margin-top: 0px; margin-bottom: 10px; display: inline;\"><span style=\"line-height: 1.5; font-size: 10pt;\">歌词展示通过网络获取，自动展示，但是由于许多MP3不是标准格式无法获取歌曲信息的会导致下载歌词失败。</span></p></li></ol><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><br/></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">源码地址：<a href=\"http://git.oschina.net/flyfox/FFPlayer\" target=\"_blank\" style=\"color: rgb(62, 98, 166); outline: 0px;\">http://git.oschina.net/flyfox/FFPlayer</a></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">界面如下图：</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><img src=\"http://ww3.sinaimg.cn/mw690/3fc7e281jw1eqebjn05cxj20je0c30wt.jpg\"/></p><p><br/></p>', '317', '1', '12', '1', '1', null, '10', null, null, null, null, null, '10', '2014-11-24', '系统管理员', null, null, '2015-01-30 17:15:46', '2015-01-30 17:15:46', '1');
INSERT INTO `tb_article_copy` VALUES ('3108', '243', 'JavaFX游戏 贪吃蛇Snake', '<p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">JavaFX Snake是一个通过JavaFX实现的贪吃蛇游戏，游戏比较简单就不用再介绍了。</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">源码地址：<a href=\"http://git.oschina.net/flyfox/GameSnake\" target=\"_blank\" style=\"color: rgb(62, 98, 166); outline: 0px;\">http://git.oschina.net/flyfox/GameSnake</a></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">界面比较简陋凑合看吧，如下图：</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><img src=\"http://ww1.sinaimg.cn/mw690/3fc7e281jw1eqebcgnuthj20bh0980ss.jpg\"/></p><p><br/></p>', '422', '0', '12', '1', '1', null, '19', null, null, null, null, null, '10', '2014-11-04', '系统管理员', null, null, '2015-01-30 17:18:27', '2015-01-30 17:18:27', '1');
INSERT INTO `tb_article_copy` VALUES ('3109', '243', '个人博客jflyfox', '<p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><span style=\"line-height: 1.5; font-size: 10pt;\">这是一个简单不的不能再简单的Blog软件，本着提高自己，帮助新人的态度。</span><span style=\"font-size: 10pt; line-height: 1.5;\">刚刚开始做，还望大家多多包涵。</span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">Jflyfox采用了简洁强大的JFinal作为web框架，模板引擎用的是beelt，数据库用mysql，前端bootstrap框架。</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">源码地址：<a href=\"http://git.oschina.net/flyfox/jflyfox\" target=\"_blank\" style=\"color: rgb(62, 98, 166); outline: 0px;\">http://git.oschina.net/flyfox/jflyfox</a></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><span style=\"line-height: 1.5; font-size: 10pt;\">运行效果：<a href=\"http://jflyfox.oschina.mopaas.com/\" target=\"_blank\" style=\"color: rgb(62, 98, 166); outline: 0px;\">http://jflyfox.oschina.mopaas.com/</a></span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><span style=\"line-height: 1.5; font-size: 10pt;\">首页效果图如下：</span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><span style=\"line-height: 1.5; font-size: 10pt;\"><img src=\"http://ww4.sinaimg.cn/mw690/3fc7e281jw1eqebcg72juj20s00ftaf9.jpg\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">后台登陆：<a href=\"http://jflyfox.oschina.mopaas.com/admin\" target=\"_blank\" style=\"color: rgb(62, 98, 166); outline: 0px;\">http://jflyfox.oschina.mopaas.com/admin</a></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><img src=\"http://ww2.sinaimg.cn/mw690/3fc7e281jw1eqebchm1j4j20h00ee0x5.jpg\"/></p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">账号密码：admin/admin123,test/123456</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\">后台页面页面效果图如下：</p><p style=\"margin-top: 0px; margin-bottom: 10px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; line-height: 21.3333320617676px; white-space: normal;\"><img src=\"http://ww2.sinaimg.cn/mw690/3fc7e281jw1eqebcfhs3nj210v0diwjx.jpg\"/></p>', '453', '4', '12', '1', '1', null, '10', null, null, null, null, null, '10', '2015-02-02', '系统管理员', null, null, '2015-02-02 14:25:10', '2015-02-02 14:25:10', '1');
INSERT INTO `tb_article_copy` VALUES ('3111', '242', '你很棒', '<p>You look gorgeous today. 你今天看起来棒极了。</p><p>You are someting else.你真是出类拔萃。</p><p>You &#39;re looking sharp.你看上去很精神。</p><p>We&#39;re so proud of you.我们十分为你骄傲。</p><p>Thanks of for the compliments.感谢你的称赞。</p><p>i&#39;m so flattered.我真是受宠若惊。</p>', '213', '0', '11', '1', '1', null, '10', null, null, null, null, null, '10', '2015-02-04', '系统管理员', null, null, '2015-02-04 08:47:31', '2015-02-04 08:47:31', '1');
INSERT INTO `tb_article_copy` VALUES ('3112', '242', '常用单词', '<p><strong>酸甜苦辣</strong></p><p>sour acid 酸的</p><p>sweet 甜的</p><p>bitter 苦的</p><p>spicy 辣的</p><p><br/></p><p><strong>饮料</strong></p><p>apple 苹果&nbsp;apple juice 苹果汁</p><p>banana 香蕉</p><p>orange 橘子 &nbsp;orange juice 橘汁</p><p>lemon 柠檬 lemonade 柠檬水</p><p><br/></p><p><strong>日常用品</strong></p><p>pan 平底锅</p><p><br/></p>', '254', '0', '11', '1', '1', null, '10', null, null, null, null, null, '10', '2015-02-06', '系统管理员', null, null, '2015-02-06 09:18:50', '2015-02-06 09:18:50', '1');
INSERT INTO `tb_article_copy` VALUES ('3114', '247', '怎样制作支付宝二维码?', '<p style=\"margin-top: 20px; margin-bottom: 20px;\"><strong>转自：<a href=\"http://jingyan.baidu.com/article/a3a3f811e305258da3eb8a69.html\" target=\"_self\" title=\"http://jingyan.baidu.com/article/a3a3f811e305258da3eb8a69.html\">http://jingyan.baidu.com/article/a3a3f811e305258da3eb8a69.html</a></strong></p><p style=\"margin-top: 20px; margin-bottom: 20px;\">怎样制作透明底色的支付宝二维码收款大图？</p><h2 class=\"exp-content-head\" style=\"margin-top: 40px; margin-bottom: 0px; padding-bottom: 7px; font-size: 22px; font-stretch: normal; line-height: 24px; font-family: 微软雅黑; color: rgb(51, 51, 51); border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: rgb(214, 214, 216);\"><a name=\"section-2\" style=\"color: rgb(45, 100, 179);\" href=\"http://\"></a>方法/步骤</h2><ol class=\"exp-conent-orderlist list-paddingleft-2\" style=\"list-style-type: none;\"><li class=\"exp-content-list \" style=\"\"><p>1</p><p style=\"margin-top: 0px; margin-bottom: 0px;\">登陆官方网站（https://qr.alipay.com），点击账户码，免费领取“向我付款”的二维码。</p><p><img src=\"http://cdada.img43.wal8.com/img43/509782_20150213134515/142406684177.jpg\"/></p></li><li class=\"exp-content-list \" style=\"\"><p>2</p><p style=\"margin-top: 0px; margin-bottom: 0px;\">避免广告嫌疑。截图中 二维码 已做涂抹。个性化设置可以自行上传头像照片，并设置收款人姓名</p><p><img src=\"http://cdada.img43.wal8.com/img43/509782_20150213134515/142406684231.jpg\"/></p></li><li class=\"exp-content-list \" style=\"\"><p>3</p><p style=\"margin-top: 0px; margin-bottom: 0px;\">选择另存到本地。共有3种样式供选择。全部是透明底色的PNG图片。</p><p style=\"margin-top: 0px; margin-bottom: 0px;\">大(150pixel)——应用于海报、户外广告等对印刷质量要求高的渠道。建议尺寸：584*584像素（即15*15厘米，150分辨率</p><p style=\"margin-top: 0px; margin-bottom: 0px;\">中(72pixel)——应用于宣传单页等具有一定图片质量需求的渠道。建议尺寸：292*292像素（即7*7厘米，72分辨率）</p><p style=\"margin-top: 0px; margin-bottom: 0px;\">小(50pixel)——应用于PC渠道。建议尺寸：175*175像素（即5*5厘米，50分辨率）</p><p><img src=\"http://cdada.img43.wal8.com/img43/509782_20150213134515/14240668436.jpg\"/></p></li></ol><p><br/></p>', '242', '0', '11', '1', '1', null, '10', null, null, null, null, null, '10', '2015-02-13', '系统管理员', null, null, '2015-02-13 09:06:52', '2015-02-13 09:06:52', '1');
INSERT INTO `tb_article_copy` VALUES ('3115', '243', 'JavaFX游戏 打砖块', '<p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">今天学习了一下JavaFX游戏部分<br/></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">参考博客：<a href=\"http://blog.csdn.net/ml3947\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\">http://blog.csdn.net/ml3947</a></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">本人源码地址：<a href=\"http://git.oschina.net/flyfox/GameBrickBlock\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\">http://git.oschina.net/flyfox/GameBrickBlock</a></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">里面有两种效果实现，一种是参考博客的Pane实现，一种是自己学习修改的Canvas实现。</p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">效果图演示：</p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><img src=\"http://ww2.sinaimg.cn/mw690/3fc7e281jw1eqebch2eibj20mi0hjgm8.jpg\"/></p><p><br/></p>', '600', '0', '12', '1', '1', null, '20', null, null, null, null, null, '10', '2014-11-01', '系统管理员', null, null, '2015-02-13 11:20:15', '2015-02-13 11:20:15', '1');
INSERT INTO `tb_article_copy` VALUES ('3116', '243', '基础库封装', '<p><br/></p><p><strong>源码地址：<a href=\"http://git.oschina.net/flyfox/jflyfox_base\" target=\"_blank\" title=\"http://git.oschina.net/flyfox/jflyfox_base\">http://git.oschina.net/flyfox/jflyfox_base</a></strong></p><p>flyfox基础库(jflyfox_base)</p><p>作者：FLY的狐狸</p><p>功能：基础库。实现String、Number、Date、加密算法，序列化、缓存管理等封装。</p>', '544', '3', '11', '1', '1', null, '16', null, null, null, null, null, '10', '2014-01-01', '系统管理员', null, null, '2015-02-13 11:28:58', '2015-02-13 11:28:58', '1');
INSERT INTO `tb_article_copy` VALUES ('3117', '243', '基础库Jfinal封装', '<p><strong>源码地址：</strong><a href=\"http://git.oschina.net/flyfox/jflyfox_jfinal\" target=\"_blank\" title=\"http://git.oschina.net/flyfox/jflyfox_jfinal\">http://git.oschina.net/flyfox/jflyfox_jfinal</a></p><p>jflyfox_jfinal是对Jfinal和beetl进行封装。</p><p>1. 包含controller，model，form，service基础类封装。</p><p>2. 对分页进行了后台和前台的实现。</p><p>3. 加入了自动扫描model和controller以及注解支持。</p><p>4. 实现了ueditor后台Controller代码。</p><p>5. 加入了页面增删改查代码自动生成功能，可通过beetl模板进行配置。</p><p>6. 实现了SessionAttrInterceptor、页面和手机设备判断拦截器以及BasePathHandler。</p>', '484', '0', '11', '1', '1', null, '17', null, null, null, null, null, '10', '2014-01-02', '系统管理员', null, null, '2015-02-13 11:42:33', '2015-02-13 11:42:33', '1');
INSERT INTO `tb_article_copy` VALUES ('3118', '243', '通过mina进行文件传输', '<p><strong>FileSocket</strong></p><p>通过mina进行文件传输</p><p>传入目录readfile</p><p>接收目录writefile</p><p>日志目录logs</p><p>配置文件config/config.properties</p><p><br/></p><p><strong>发送流程</strong></p><p>1. 定时读取发送路径文件</p><p>2. 将文件名称加入.tmp后缀，进行流读取发送</p><p>3. 发送后将文件放入备份路径（backup）</p><p>4. 服务器端返回成功，放入成功路径（finished）；返回失败，放入失败路径（failed）。</p><p><br/></p><p><strong>注：由于是个人项目未进行开源</strong></p><p><br/></p>', '390', '0', '11', '1', '1', null, '30', null, null, null, null, null, '10', '2014-11-25', '系统管理员', null, null, '2015-02-13 11:57:00', '2015-02-13 11:57:00', '1');
INSERT INTO `tb_article_copy` VALUES ('3119', '243', '财务管理 Android项目', '<p style=\"margin-top: 8px; line-height: 22.8571434020996px; letter-spacing: 0.5px; white-space: normal;\">DataAnalysis</p><p style=\"margin-top: 8px; line-height: 22.8571434020996px; letter-spacing: 0.5px; white-space: normal;\"><span style=\"line-height: 22.8571434020996px;\">主要是记录个人收入、支出，输出月报表，年报表。</span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><a href=\"http://static.oschina.net/uploads/space/2015/0213/121009_XZVN_166354.png\" target=\"_blank\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\"><img src=\"http://ww3.sinaimg.cn/mw690/3fc7e281jw1eqeblvgkroj20e80e8aby.jpg\" style=\"width: 335px; height: 340px;\"/></a><br/></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">界面如下：</p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><br/></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><br/></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><a href=\"http://static.oschina.net/uploads/space/2015/0213/121010_i34R_166354.png\" target=\"_blank\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\"><img src=\"http://ww3.sinaimg.cn/mw690/3fc7e281jw1eqeblvshmsj20dc0m8q4t.jpg\"/></a></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><img src=\"http://ww1.sinaimg.cn/mw690/3fc7e281jw1eqeblw5dz3j20dc0m8wfc.jpg\"/></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><img src=\"http://ww4.sinaimg.cn/mw690/3fc7e281jw1eqeblwdpsej20dc0m8wfr.jpg\"/></p><p><span style=\"line-height: 22.8571434020996px;\"></span><br/></p><p><span style=\"font-weight: 700; line-height: 22.8571434020996px;\"><br/></span></p><p><span style=\"font-weight: 700; line-height: 22.8571434020996px;\">注：由于是个人项目未进行开源</span></p>', '395', '0', '11', '1', '1', null, '31', null, null, null, null, null, '10', '2014-12-13', '系统管理员', null, null, '2015-02-13 12:00:07', '2015-02-13 12:00:07', '1');
INSERT INTO `tb_article_copy` VALUES ('3121', '245', '捐赠我们', '<p style=\"text-align: center;\">支付宝捐赠二维码 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 微信捐赠二维码</p><p style=\"text-align: center;\"><img src=\"http://ww1.sinaimg.cn/mw690/3fc7e281jw1eqec436tzwj2074074mxr.jpg\"/>&nbsp; &nbsp;&nbsp;<img src=\"http://ww1.sinaimg.cn/mw690/3fc7e281jw1es3jr0k25xj20a50a5q3v.jpg\" style=\"line-height: 1.42857143; width: 256px; height: 256px;\"/></p><p style=\"text-align: center;\"><br/><br/></p>', '830', '1', '12', '1', '1', null, '12', null, null, null, null, null, '10', '2015-02-13', '系统管理员', null, null, '2015-02-13 12:23:37', '2015-02-13 12:23:37', '1');
INSERT INTO `tb_article_copy` VALUES ('3206', '242', '人的器官', '<p>鼻子 nose</p><p>脸 face</p><p>嘴 mouth</p><p>眼睛 eye</p><p>耳朵 ear</p><p>手 head</p><p>腿 leg</p><p>脚 foot</p><p>手指 finger</p><p>拳头 fist</p><p>屁股 behind</p>', '236', '0', '11', '1', '1', null, '10', null, null, null, null, null, '10', '2015-02-26', '系统管理员', null, null, '2015-02-26 09:14:47', '2015-02-26 09:14:47', '1');
INSERT INTO `tb_article_copy` VALUES ('3207', '247', '离职理由', '<ul style=\"margin-top: 10px; margin-bottom: 20px; margin-left: 20px; list-style-position: inside; line-height: 20px; color: rgb(0, 0, 0); font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 14px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">目前公司</span><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">决策上太过拖沓，工作太清闲，不够饱和，对个人成长不利，所以考虑换份工作</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">对目前公司的技术框架比较熟悉，希望能接触更多的有挑战性的开发工作，所以考虑新的机会。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">目前的工作内容跟我个人的期望有差距，与个人职业发展不符，所以考虑换家公司。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">目前公司是家创业公司，在经营方面出了些问题，导致公司发展停滞，不得不另谋出路，寻求更好的发展机会。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">目前公司管理混乱，做事效率低，资金链断裂导致项目终止，所以希望找份新工作。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">身处二线城市，互联网环境较差，缺乏技术提升和与同行交流的大环境，所以考虑来上海发展。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">公司业务调整，目前所负责的项目被边缘化，以后不做新功能开发只做维护，个人希望逐步向技术管理方向发展，但在公司内部缺乏提升的空间，所以考虑换个环境。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">领导对业务没有长远规划，加上领导层频繁变动，故考虑新的机会。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">公司业务调整，我所负责的项目被叫停了，在公司没有太多事情做，所以希望换个环境。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">公司调整管理风格改变，和我期望的互联网文化大相径庭，所以考虑新的工作机会。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">目前在朋友公司帮忙，现技术框架已经相对固定，后续的工作主要是系统维护和优化，没有太多事情做，所以考虑合适的工作机会。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">在目前公司工作了多年，发展到了瓶颈，希望换个环境。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">在目前公司工作不饱和，内部缺乏职位和待遇提升空间，所以考虑新的工作机会。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">公司开发项目及架构搭建已全部完成，后续的工作主要是系统维护和优化，工作不饱和，缺乏挑战性，故考虑新的工作机会。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">公司高层频繁变动，发展不稳定，希望看看新的机会。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">目前公司是家传统行业公司，技术研发只是给主营业务提供IT支撑，公司对技术不够重视，不利于我在技术研发领域深入钻研，所以考虑换个环境。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">家在北方，希望离家近一点，所以决定回北京发展。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">目前负责的项目侧重内部业务支撑，个人更希望做一些C端用户类产品方面的开发，所以考虑新的工作机会。</span></p></li><li><p><span style=\"color: rgb(148, 148, 148); font-family: 微软雅黑, &#39;Open Sans&#39;, Helvetica, Arial, sans-serif; line-height: 25.2000007629395px; font-size: 10pt;\">目前所负责的项目并非领导的关注重点，目前进展不太顺利，可能会被叫停，我在公司内缺乏提升空间，所以考虑换个环境。</span></p></li></ul><p><br/></p>', '267', '0', '11', '1', '1', null, '10', null, null, null, null, null, '10', '2015-03-06', '系统管理员', null, null, '2015-03-06 09:19:58', '2015-03-06 09:19:58', '1');
INSERT INTO `tb_article_copy` VALUES ('3209', '241', '三种人', '<p><span style=\"font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; letter-spacing: 0.5px; line-height: 21px;\">人的一生总是要学习各种知识。人人都在学习，但学习方法却各有不同。</span><br/></p><p><span style=\"letter-spacing: 0.5px; font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px;\">1 绝大多数人是闭门学习受填鸭式学习教育遗毒影响，独自一个人挑灯苦灯，学习吸收各种知识。</span><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><span style=\"letter-spacing: 0.5px; font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px;\">这种学习方式比较枯燥，需要比较好的耐力，毅力。</span><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><span style=\"letter-spacing: 0.5px; font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px;\">2 一部分重视通过交流学习现在网络比较发达了，许多人自觉不自觉得把自己的学习心得记录下来，并发表在网上。</span><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><span style=\"letter-spacing: 0.5px; font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px;\">3 也有人通过启发别人来启发自己其中的哲学原理，按古代说法是天人合一，物我同体；按现代说法是信息相通，沟通无极限。</span><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><span style=\"letter-spacing: 0.5px; font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px;\">通过帮助别人来帮助自己。通过启发别人来启发自己。凡有所得，必须分享。只有分享了的知识，才是真正的知识。</span><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><span style=\"letter-spacing: 0.5px; font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px;\">上面三种人，对于知识而言，</span><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><span style=\"letter-spacing: 0.5px; font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px;\">第一种人是只进不出，只是获取别人总结的知识，而基本不去帮助别人获取知识</span><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><span style=\"letter-spacing: 0.5px; font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px;\">第二种人是多进少出，在学习知识的同时，也把自己的一些心得分享出来</span><br style=\"font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; letter-spacing: 0.5px; line-height: 22.5px; white-space: normal;\"/><span style=\"letter-spacing: 0.5px; font-family: SimSun, Verdana, Arial, Helvetica, sans-serif; font-size: 14px; line-height: 21px;\">第三种人趋于得失平衡，认为只有分享的知识才是真正的知识，只有帮助别人也掌握的知识才是真正的知识</span></p>', '645', '13', '12', '1', '1', '2', '9', null, null, null, null, null, '10', '2015-03-13', '系统管理员', null, null, '2015-03-13 09:14:47', '2015-03-13 09:14:47', '1');
INSERT INTO `tb_article_copy` VALUES ('3210', '249', 'wireshark抓包分析', '<ul style=\"margin-left: 20px; line-height: 22.5px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p style=\"margin-top: 8px; margin-bottom: 8px; letter-spacing: 0.5px; font-size: 12.5px;\"><strong>&nbsp;<span style=\"font-size: 18px;\">抓包</span></strong></p></li></ul><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">命令：<span style=\"font-size: 12.5px;\">tcpdump&nbsp;tcp&nbsp;-s&nbsp;0&nbsp;port&nbsp;8888 -w&nbsp;test.cap&nbsp;</span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">linux上通过tcpdump命令进行数据报抓取，tcp协议，-s 0 抓取完整包（默认大小<span style=\"font-family: Verdana, Geneva, Arial, Helvetica, sans-serif; line-height: 19.5px;\">68字节</span>），监控888端口，<span style=\"font-family: Verdana, Geneva, Arial, Helvetica, sans-serif; line-height: 19.5px;\">保存成test.cap文件</span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">tcpdump命令详解：<a href=\"http://www.cnblogs.com/ggjucheng/archive/2012/01/14/2322659.html\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\">http://www.cnblogs.com/ggjucheng/archive/2012/01/14/2322659.html</a></p><ul style=\"margin-left: 20px; line-height: 22.5px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; font-size: 13px; white-space: normal;\" class=\" list-paddingleft-2\"><li><p style=\"margin-top: 8px; margin-bottom: 8px; letter-spacing: 0.5px; font-size: 12.5px;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: Verdana, Geneva, Arial, Helvetica, sans-serif; line-height: 19.5px;\">文件分析</span></strong></span></p></li></ul><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><span style=\"font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><span style=\"line-height: 19.5px;\">通过wireshark打开test.cap文件，就可以进行分析了。</span></span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><strong><span style=\"font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><span style=\"line-height: 19.5px;\">首先对连接和断开进行分析，看是否有异常情况。</span></span></strong></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><span style=\"font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><span style=\"line-height: 19.5px;\">网上找了个链接和断开的图片，不是很清楚凑合看。</span></span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><span style=\"font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><span style=\"line-height: 19.5px;\"><a href=\"http://static.oschina.net/uploads/space/2014/1212/105335_1ThN_166354.jpg\" target=\"_blank\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\"><img src=\"/jflyfox/ueditor/image/20160612/1465695387120046142.jpg\" style=\"padding: 5px; margin-top: 10px; margin-bottom: 10px; border: 1px solid rgb(221, 221, 221); max-width: 640px; cursor: pointer; background: rgb(244, 247, 249);\"/></a></span></span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><span style=\"font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\"><span style=\"line-height: 19.5px;\">上面图可以看出连接是syn标示，断开是fin标示，通过 tcp.flags.fin == 1 || tcp.flags.syn== 1 可以进行过滤找出，断开和连接的数据包。可以查看里面的时间点和stream index数据。</span></span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><span style=\"line-height: 19.5px;\">通过时间点或者tcp.stream == 7过滤进行会话跟踪，查看附近相关数据包，从而进行异常数据排查。</span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><strong><span style=\"line-height: 19.5px;\">数据分析</span></strong></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><span style=\"line-height: 19.5px;\">我们发送的数据自己对协议都应该比较了解，tcp.len == 140对数据包进行过滤，查看具体协议内容，找出异常信息。</span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><span style=\"line-height: 19.5px;\"><span style=\"font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\">wireshark</span>过滤语法：<a href=\"http://www.cnblogs.com/wangkangluo1/archive/2011/12/19/2293750.html\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\">http://www.cnblogs.com/wangkangluo1/archive/2011/12/19/2293750.html</a></span></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\"><br/></p><p style=\"margin-top: 8px; margin-bottom: 8px; line-height: 22.5px; letter-spacing: 0.5px; font-size: 13px; font-family: 微软雅黑, Verdana, sans-serif, 宋体; white-space: normal;\">抓包:tcpdump &nbsp;-i eth1 -s 0 tcp port 80 -w &nbsp; loghttp.cap&nbsp;</p><p><br/></p>', '209', '0', '11', '1', '1', null, '10', null, null, null, null, null, '10', '2015-03-13', '系统管理员', null, null, '2015-03-13 09:25:05', '2015-03-13 09:25:05', '1');
INSERT INTO `tb_article_copy` VALUES ('3211', '249', '各大音乐社区api接口（MP3&LRC）', '<p style=\"margin-top: 0px; margin-bottom: 8px; line-height: 26px; letter-spacing: 0.5px; font-size: 14px; white-space: normal; font-family: Arial;\"><strong>百度：</strong><br/>sound:<br/><a href=\"http://box.zhangmen.baidu.com/x?op=12&count=1&title=%B2%BB%B5%C3%B2%BB%B0%AE$$%C5%CB%E7%E2%B0%D8$$$$\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\">http://box.zhangmen.baidu.com/x?op=12&amp;count=1&amp;title=不得不爱$$潘玮柏$$$$</a><br/>lrc:<br/><a href=\"http://box.zhangmen.baidu.com/bdlrc/86/8654.lrc\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\">http://box.zhangmen.baidu.com/bdlrc/86/8654.lrc</a><br/>这个地址解析下:&nbsp;<br/>http://box.zhangmen.baidu.com/bdlrc/&nbsp;这个是百度lrc歌词存放地址,后面的496是一个的不定的,民就是说歌曲不同那个目录名也不同,它的算法是拿歌词文件名(也就是上面说的&nbsp;8654)&nbsp;除以一百,然后取小于等于其结果的最大整数,如上面的:8654/100&nbsp;=86.54取小于等于86.54&nbsp;的最大整数就是86,于是这首歌完整的歌词地址:<a href=\"http://box.zhangmen.baidu.com/bdlrc/86/8654.lrc\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\">http://box.zhangmen.baidu.com/bdlrc/86/8654.lrc</a></p><p style=\"margin-top: 0px; margin-bottom: 8px; line-height: 17.142858505249px; letter-spacing: 0.5px; font-size: 12px; white-space: normal; color: rgb(61, 68, 80); font-family: &#39;Microsoft Yahei&#39;, Helvetica, Arial, sans-serif;\"><strong>QQ音乐：</strong><br/>sound:<br/><a href=\"http://qqmusic.qq.com/fcgi-bin/qm_getLyricId.fcg?name=%B2%BB%B5%C3%B2%BB%B0%AE&singer=%C5%CB%E7%E2%B0%D8&from=qqplayer\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px;\">http://qqmusic.qq.com/fcgi-bin/qm_getLyricId.fcg?name=不得不爱&amp;singer=潘玮柏&amp;from=qqplayer</a><br/>lrc:<br/><a href=\"http://music.qq.com/miniportal/static/lyric/90/95690.xml\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px;\">http://music.qq.com/miniportal/static/lyric/90/95690.xml</a></p><p style=\"margin-top: 0px; margin-bottom: 8px; line-height: 26px; letter-spacing: 0.5px; font-size: 14px; white-space: normal; font-family: Arial;\">&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 8px; line-height: 26px; letter-spacing: 0.5px; font-size: 14px; white-space: normal; font-family: Arial;\">说明：lrc的歌词地址&nbsp;lyric/90/95690.xm 其中的90是上面sound中出来的id95690的最后面两位数字、。<br/>&nbsp;<br/><strong>搜搜音乐：</strong><br/>sound:<br/><a href=\"http://cgi.music.soso.com/fcgi-bin/m.q?w=%B2%BB%B5%C3%B2%BB%B0%AE&p=1&t=0\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\">http://cgi.music.soso.com/fcgi-bin/m.q?w=<span style=\"color: rgb(0, 0, 0);\">不得不爱</span>&amp;p=1&amp;t=0</a><br/>格式:&nbsp;<span style=\"color: rgb(192, 0, 0);\">1</span>-全部&nbsp;<span style=\"color: rgb(192, 0, 0);\">0</span>-MP3&nbsp;<span style=\"color: rgb(192, 0, 0);\">1</span>-RM&nbsp;，<span style=\"color: rgb(192, 0, 0);\">2</span>-WMA<br/>lrc:(注意！歌曲名和歌手名字一定要准确)<br/><a href=\"http://cgi.music.soso.com/fcgi-bin/fcg_download_lrc.q?song=%B2%BB%B5%C3%B2%BB%B0%AE&singer=%C5%CB%E7%E2%B0%D8%26%CF%D2%D7%D3&down=1\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px; font-size: 12px;\">http://cgi.music.soso.com/fcgi-bin/fcg_download_lrc.q?song=不得不爱&amp;singer=潘玮柏%26弦子&amp;down=1</a></p><p style=\"margin-top: 0px; margin-bottom: 8px; line-height: 17.142858505249px; letter-spacing: 0.5px; font-size: 12px; white-space: normal; color: rgb(61, 68, 80); font-family: &#39;Microsoft Yahei&#39;, Helvetica, Arial, sans-serif;\">&nbsp;<br/><strong>8BOX</strong><br/>sound:<br/><a href=\"http://api.8box.com/get/search/song?api_key=%7Bapi_key%7D&title=%7B%B8%E8%C7%FA%C3%FB%7D&artist=%7B%B8%E8%CA%D6%7D\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px;\">http://api.8box.com/get/search/song?api_key={api_key}&amp;title={歌曲名<span style=\"color: rgb(0, 0, 0);\">（URL16进制加密）</span>}&amp;artist={歌手<span style=\"color: rgb(0, 0, 0);\">（URL16进制加密）</span>}</a><br/><span style=\"font-family: 宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><br/>&nbsp;lrc:(2010年8月19日更新)<br/><a href=\"http://www.8box.com/feed/radio/s?type=widget&param\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px;\">http://www.8box.com/feed/radio/s?type=widget&amp;param</a>={歌曲ID(数字)}<br/><a href=\"http://www.8box.com/pictures/lyrics/83/591383.lrc\" target=\"_blank\" rel=\"nofollow\" style=\"color: rgb(255, 131, 115); outline: 0px;\">http://www.8box.com/pictures/lyrics/83/591383.lrc</a></p><p><br/></p>', '210', '0', '11', '1', '1', null, '10', null, null, null, null, null, '10', '2015-03-13', '系统管理员', null, null, '2015-03-13 09:26:25', '2015-03-13 09:26:25', '1');
INSERT INTO `tb_article_copy` VALUES ('3213', '242', '英语学习', '<h2 style=\"line-height: 22.8571px; white-space: normal;\">学习记录</h2><p style=\"line-height: 22.8571434020996px; white-space: normal;\">你的爱好是什么？</p><p style=\"line-height: 22.8571434020996px; white-space: normal;\">what do you like to do for fun?</p><p style=\"line-height: 22.8571434020996px; white-space: normal;\">what do you do in your free time?</p><p style=\"line-height: 22.8571434020996px; white-space: normal;\">what are your hobbies？</p><p><br/></p><p>Comedies喜剧片、dramas剧情片和thrillers惊险片</p><p><br/></p><p><span style=\"line-height: 22.8571434020996px;\">很高兴我们有一些共同点</span></p><p>It&#39;s nice that we have something in common</p><p><br/></p><h2>口语常用语句</h2><p><br/></p><p>1.Absolutely. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; (用于答话）是这样;当然是;正是如此;绝对如此。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>2.Absolutely &nbsp;impossible! &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; 绝对不可能的！ &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>3.All I have to do is learn English. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p>&nbsp; 我所要做的就是学英语。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>4.Are you free tomorrow? &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p>&nbsp; 你明天有空吗? &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p><br/></p><p>5.Are you married? &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p>&nbsp; 你结婚了吗？ &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>6.Are you used to the food here? &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p>&nbsp; 你习惯吃这儿的饭菜吗？ &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>7.Be careful. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; 小心／注意。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>8.Be my guest. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p>&nbsp; 请便／别客气。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>9.Better late than never. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; 迟到总比不到好。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>10.Better luck next time. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p>&nbsp; &nbsp;祝你下一次好运。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p><br/></p><p>11.Better safe than sorry. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><p>&nbsp; &nbsp;小心不出大错。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;